import React, { useState } from "react";

import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  console.log("App!!!");
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);
  const [subExpenses, setSubExpenses] = useState(DUMMY_EXPENSES);
  console.log("expenses : " + expenses);

  const addExpenseHandler = (expense) => {
    console.log("addExpenseHandler!!!");
    console.log("expense.title : " + expense.title);
    console.log("expense.id : " + expense.id);
    console.log("expense.date : " + expense.date);
    setExpenses((prevExpenses) => {
      console.log(...prevExpenses);
      return [expense, ...prevExpenses];
    });
  };

  const selectFilterHandler = (selectedYear) => {
    console.log("selectFilterHandler!!!");
    console.log("selectedYear : " + selectedYear);
    console.log("expenses : " + expenses);
    setSubExpenses((expenses) => {
      subExpenses = expenses.filter(
        (data) => data.date.getFullYear() === selectedYear
      );
      return subExpenses;
    });
    console.log("subExpenses : " + subExpenses);
  };

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses items={expenses} onSelectFilter={selectFilterHandler} />
    </div>
  );
};

export default App;
